SELECT tblCoreSiteInformationPanel.[CENTER NBR], tblCoreSiteInformationPanel.[CENTER NAME], tblCoreSiteInformationPanel.CITY, tblCoreSiteInformationPanel.State, tblCoreSiteInformationPanel.COUNTRY_NAME, tblConstructionSchedule.[Project Number], tblTakeoff.[Vendor Name], tblTakeoff.[Category Name], tblTakeoff.[Category Number], tblTakeoff.[Item Number], tblTakeoff.[Item Account Number], tblTakeoff.[Item Quantity], IIf([Vendor Name]="idX Los Angeles" And [tblTakeoff].[Comments] Is Not Null,[Item Description] & " (" & [tblTakeoff].[Comments] & ")",[Item Description]) AS Description
FROM (tblCoreSiteInformationPanel LEFT JOIN tblConstructionSchedule ON tblCoreSiteInformationPanel.[CENTER NBR] = tblConstructionSchedule.[Site Number]) LEFT JOIN tblTakeoff ON tblConstructionSchedule.[Project Number] = tblTakeoff.[Project Number]
WHERE (((tblConstructionSchedule.[Project Number])=Forms!frmCoreSiteInformationPanel!fsubCoreProjectInformationPanel!txtProjectNumber) And ((tblTakeoff.[Item Quantity])>0));

