SELECT tblCoreSiteInformationPanel.[CENTER NAME], tblCoreSiteInformationPanel.[Site Number], "" AS [Legal Entity Name], "" AS [Tax ID], tblCoreSiteInformationPanel.Telephone, tblCoreSiteInformationPanel.[ADDRESS 1], tblCoreSiteInformationPanel.[ADDRESS 2], tblCoreSiteInformationPanel.CITY, tblCoreSiteInformationPanel.State, tblCoreSiteInformationPanel.ZIP_CODE, "Yes" AS Lease, tblCoreSiteInformationPanel.LANDLORD AS [Landlord Name], tblCoreSiteInformationPanel.Telephone AS [Landlord Telephone], tblConstructionSchedule.Turnover AS [Date Utility Accounts to be Opened by], tblConstructionSchedule.[Actual Turnover], tblConstructionSchedule.Open, tblUtilities.[Electric Not Applicable], tblRetailOperations.[Electric Supplier Meter], tblUtilities.[Gas Not Applicable], tblRetailOperations.[Gas Supplier Meter], tblUtilities.[Water Not Applicable], tblRetailOperations.[Water Supplier Meter], tblCoreSiteInformationPanel.[SQUARE FOOTAGE] AS [Square Footage], tblCoreSiteInformationPanel.REGION AS Region, tblCoreSiteInformationPanel.DISTRICT AS District, "" AS [Responsibility Code], tblCoreSiteInformationPanel.[COMPANY NBR]
FROM ((tblCoreSiteInformationPanel LEFT JOIN tblRetailOperations ON tblCoreSiteInformationPanel.[CENTER NBR] = tblRetailOperations.[Site Number]) INNER JOIN tblConstructionSchedule ON tblCoreSiteInformationPanel.[CENTER NBR] = tblConstructionSchedule.[Site Number]) LEFT JOIN tblUtilities ON tblConstructionSchedule.[Project Number] = tblUtilities.[Project Number]
WHERE (((tblConstructionSchedule.Turnover) Between [Enter Start Turnover Date] And [Enter End Turnover Date]) AND ((tblRetailOperations.[Utilities Complete])=False) AND ((tblConstructionSchedule.[Project Status])="A"));

