SELECT tblCoreSiteInformationPanel.[CENTER NBR], tblCoreSiteInformationPanel.[CENTER NAME], tblCoreSiteInformationPanel.[ADDRESS 1], tblCoreSiteInformationPanel.[ADDRESS 2], tblCoreSiteInformationPanel.CITY, tblCoreSiteInformationPanel.State, tblCoreSiteInformationPanel.ZIP_CODE, tblCoreSiteInformationPanel.COUNTRY_NAME, tblCoreSiteInformationPanel.DIVISION, tblCoreSiteInformationPanel.REGION, [Phone area code] & "-" & [Phone Exchange] & "-" & [phone nbr] AS [Store Number], tblConstruction.[Landlord Contact Name], tblConstruction.[Landlord Contact Telephone], tblConstruction.[Landlord Contact Email], qurFacilitiesUpdateStepOne.[RSD Name], qurFacilitiesUpdateStepOne.[Cell Phone Number], tblConstructionSchedule.Open
FROM (((tblCoreSiteInformationPanel LEFT JOIN [CMF INFO] ON tblCoreSiteInformationPanel.[CENTER NBR] = [CMF INFO].[CENTER NBR]) LEFT JOIN qurFacilitiesUpdateStepOne ON ([CMF INFO].REGION = qurFacilitiesUpdateStepOne.RegionFormat) AND ([CMF INFO].DIVISION = qurFacilitiesUpdateStepOne.DivisionFormat)) LEFT JOIN tblConstructionSchedule ON tblCoreSiteInformationPanel.[CENTER NBR] = tblConstructionSchedule.[Site Number]) LEFT JOIN tblConstruction ON tblConstructionSchedule.[Project Number] = tblConstruction.[Project Number]
WHERE (((tblConstructionSchedule.Open) Between Date() And Date()+90) AND ((tblConstructionSchedule.Category)="N") AND ((tblCoreSiteInformationPanel.[Corporate Store or Franchise Store])="Corporate Store") AND ((tblConstructionSchedule.[Project Status])="A"));

