SELECT qurReportingBase.[Site Number], qurReportingBase.[Project Number], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.COUNTRY_NAME, qurReportingBase.DIVISION, qurReportingBase.[Corporate or Franchise], qurReportingBase.Location, qurReportingBase.[Construction Started], qurReportingBase.[Construction Completed], qurReportingBase.Open, qurReportingBase.Contractor, [State Abbreviations].State, tblCategory.Category, tblProjectManager.[Project Manager], tblContract.[Purchase Order Number], tblContract.[Contract Date], tblContract.[Contract Received], tblContract.[Contract Returned], tblContract.[Contract Amount], tblContract.[Application One Complete], tblContract.[Partial Lien Waivers], tblContract.[Certificate of Insurance], tblContract.[Building Permit], tblContract.[Subcontractor List], tblContract.[Application One Sent to AP], tblContract.[Application One Amount], tblContract.[Application Two Complete], tblContract.[Final Waivers], tblContract.Warranties, tblContract.[G706 and G706A], tblContract.[Certificate of Occupancy], tblContract.[Signed Punchlist], tblContract.Pictures, tblContract.[As-Built Plans], tblContract.[Statutory Declaration (Canada)], tblContract.[Clearance Certificate (Canada)], tblContract.[Application Two Sent to AP], tblContract.[Application Two Amount], tblContract.[Miscellaneous Complete], tblContract.[5-Year Compressor Warranty], tblContract.[Air Balance Report], tblContract.[Approved Sprinkler Drawing], tblContract.[Notice of Completion], tblContract.[Cash Allowance], tblContract.[Date Information Required]
FROM (((qurReportingBase LEFT JOIN tblContract ON qurReportingBase.[Project Number] = tblContract.[Project Number]) LEFT JOIN tblCategory ON qurReportingBase.Category = tblCategory.Abbreviation) LEFT JOIN tblProjectManager ON qurReportingBase.[Project Manager] = tblProjectManager.Initials) LEFT JOIN [State Abbreviations] ON qurReportingBase.ST = [State Abbreviations].[State Abbrev]
WHERE (((qurReportingBase.[Project Number])=[Forms]![frmCoreSiteInformationPanel]![fsubCoreProjectInformationPanel]![txtProjectNumber]));

