SELECT [CMF INFO].[CENTER NBR], [CMF INFO].[CENTER NAME], [CMF INFO].[ADDRESS 1], [CMF INFO].[ADDRESS 2], tblCoreSiteInformationPanel.CITY, [CMF INFO].ST, [CMF INFO].[ZIP CODE], tblCoreSiteInformationPanel.COUNTRY_NAME, [Phone Area Code] & "-" & [Phone Exchange] & "-" & [Phone NBR] AS Phone, tblCoreSiteInformationPanel.Location, tblCoreSiteInformationPanel.[Corporate Store or Franchise Store] AS [Corporate or Franchise], IIf([PRIMARY_DC]="001","Leetsdale",IIf([PRIMARY_DC]="003","Anderson",IIf([PRIMARY_DC]="004","Phoenix",IIf([PRIMARY_DC]="005","Indy",IIf([PRIMARY_DC]="006","Canada",[PRIMARY_DC]))))) AS [Distribution Center], [CMF INFO].DIVISION, tblCoreSiteInformationPanel.DISTRICT, [CMF INFO].REGION, IIf([PLANOGRAM_CODE]="21","21 - A",IIf([PLANOGRAM_CODE]="22","22 – Modified A",IIf([PLANOGRAM_CODE]="23","23 - B",IIf([PLANOGRAM_CODE]="24","24 - C",IIf([PLANOGRAM_CODE]="25","25 - XP",IIf([PLANOGRAM_CODE]="31","31 – Canada A",IIf([PLANOGRAM_CODE]="32","32 – Canada B",IIf([PLANOGRAM_CODE]="61","61 - Military",[PLANOGRAM_CODE])))))))) AS Segment, IIf([Location]="Mall","Ceramic",IIf([Location]="Strip","Vinyl",IIf([Location]="Downtown","Ceramic",IIf([Location]="Military Base","Vinyl",IIf([Location]="Outlet","Vinyl",[Location]))))) AS Flooring, "True" AS [Store Owned Refrigerator Brand], "GDM-33-54-SSL White" AS [Store Owned Refrigerator Model], "Yes" AS [Built-In Cab w/ Cold Drink], "No" AS [Two-Sided Pass Thru], "No" AS [Small Refrig Under CW], "" AS [Number of Shelves], "" AS [Open Door], "" AS [Slide Door], "" AS [Glambia Refrigerator], "No" AS [3x2 Light Boxes in Sports Section], "3" AS [Small Lock Boxes], "3" AS [Large Lock Boxes], tblConstructionSchedule.[Cash Wrap Size] AS [CW Size], tblConstructionSchedule.[Cash Wrap Location] AS [CW Location], "Yes" AS [CW End Deck], IIf([Cash Wrap Location]="Side","Bracket Shelving",IIf([Cash Wrap Location]="Center (Front Facing Only)","None",IIf([Cash Wrap Location]="Center (Back-to-Back)","None",IIf([Cash Wrap Location]="Center (Side-to-Side)","None",[Cash Wrap Location])))) AS [Side CW Back Wall Shelving], IIf([Cash Wrap Location]="Side","Yes",IIf([Cash Wrap Location]="Center (Front Facing Only)","No",IIf([Cash Wrap Location]="Center (Back-to-Back)","No",IIf([Cash Wrap Location]="Center (Side-to-Side)","No",[Cash Wrap Location])))) AS [Side CW Ceiling Grid], IIf([Cash Wrap Location]="Side","Yes",IIf([Cash Wrap Location]="Center (Front Facing Only)","No",IIf([Cash Wrap Location]="Center (Back-to-Back)","No",IIf([Cash Wrap Location]="Center (Side-to-Side)","No",[Cash Wrap Location])))) AS [Back Cash Counter], [CMF INFO].nbr_pos_register, [CMF INFO].nbr_pos_register AS [L-Boxes], IIf([Cash Wrap Size]="6","1",IIf([Cash Wrap Size]="9","2",IIf([Cash Wrap Size]="12","3",IIf([Cash Wrap Size]="Other","",[Cash Wrap Size])))) AS [Shelved Sections on Front CW], tblCoreSiteInformationPanel.[Sales Area Square Footage], [Square_Footage]-[Sales Area Square Footage] AS [Back Room SQFT], "NA" AS Bars_Foods_Segment, "NA" AS Health_Beauty_Segment, "NA" AS Herbs_Segment, "NA" AS Gen_Merchandise_Segment, "NA" AS Vitamins_Wellness_Segment, "NA" AS Weight_Management_Segment, "NA" AS Protein_Performance_Segment
