SELECT [1 98Renewals_Report_All].[CENTER GLA], quniLeaseInformationOne.Project, quniLeaseInformationOne.Project_Status, quniLeaseInformationOne.CS_Dist, quniLeaseInformationOne.IH, quniLeaseInformationOne.Const_Comments, quniLeaseInformationOne.RL_Out, quniLeaseInformationOne.RL_Rec, quniLeaseInformationOne.GNCS, quniLeaseInformationOne.FS, quniLeaseInformationOne.Lease_Extract_to_AP, quniLeaseInformationOne.Rent_Start_Date, quniLeaseInformationOne.Rent_Start, quniLeaseInformationOne.Comments, quniAttorney.First & " " & quniAttorney.Last AS Attorney, quniParalegal.First & " " & quniParalegal.Last AS Paralegal, quniRepresentative.First & " " & quniRepresentative.Last AS [Real Estate Representative], IIf([Lease_Type]=1,"Developer or Landlord Lease",IIf([Lease_Type]=2,"GNC Lease",IIf([Lease_Type]=3,"Franchisee Lease"))) AS [Lease Type]
FROM (((quniLeaseInformationOne LEFT JOIN [1 98Renewals_Report_All] ON quniLeaseInformationOne.KK=[1 98Renewals_Report_All].[KK#]) LEFT JOIN quniParalegal ON quniLeaseInformationOne.Para=quniParalegal.Initials) LEFT JOIN quniAttorney ON quniLeaseInformationOne.Atty=quniAttorney.Initials) LEFT JOIN quniRepresentative ON quniLeaseInformationOne.RE=quniRepresentative.Initials
WHERE (((quniLeaseInformationOne.KK)=Forms!frmCoreSiteInformationPanel!txtSiteNumber))
ORDER BY quniLeaseInformationOne.CS_Dist DESC;

