SELECT qurReportingBase.[Site Number], qurReportingBase.DIVISION, qurReportingBase.REGION, qurReportingBase.[Corporate or Franchise], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.ST, qurReportingBase.Location, qurReportingBase.Category, qurReportingBase.Footage, qurReportingBase.Open, qurReportingBase.[Non-Sports Gondola Sections], qurReportingBase.[Non-Sports Left Wall Section], qurReportingBase.[Non-Sports Back Wall Section], qurReportingBase.[Non-Sports Right Wall Section], qurReportingBase.[Sports Gondola Sections], qurReportingBase.[Sports Left Wall Section], qurReportingBase.[Sports Back Wall Section], qurReportingBase.[Sports Right Wall Section], [Non-Sports Gondola Sections]+[Non-Sports Left Wall Section]+[Non-Sports Back Wall Section]+[Non-Sports Right Wall Section] AS [Total Non-Sports Sections], [Sports Gondola Sections]+[Sports Left Wall Section]+[Sports Back Wall Section]+[Sports Right Wall Section] AS [Total Sports Sections], qurReportingBase.[SQUARE FOOTAGE], qurReportingBase.[Sales Area]
FROM qurReportingBase
WHERE (((qurReportingBase.[Corporate or Franchise])="C") AND ((qurReportingBase.Category)="N") AND ((qurReportingBase.Open) Between Date() And Date()+60) AND ((qurReportingBase.[Project Status])="A"))
ORDER BY qurReportingBase.Open;

