SELECT tblCoreSiteInformationPanel.[CENTER NBR] AS [KK#], tblCoreSiteInformationPanel.[CENTER NAME], tblCoreSiteInformationPanel.[ADDRESS 1], tblCoreSiteInformationPanel.[ADDRESS 2], tblCoreSiteInformationPanel.CITY, tblCoreSiteInformationPanel.State, tblCoreSiteInformationPanel.ZIP_CODE, tblCoreSiteInformationPanel.[County Name], tblCoreSiteInformationPanel.Telephone, tblConstructionSchedule.Open, tblCoreSiteInformationPanel.DIVISION, tblCoreSiteInformationPanel.DISTRICT, tblCoreSiteInformationPanel.REGION, tblConstructionSchedule.[Project Manager], tblConstructionSchedule.Contractor, tblCoreSiteInformationPanel.Location, tblCompleteTaxRequirements.Complete, tblCompleteTaxRequirements.[Complete Comments]
FROM (tblCoreSiteInformationPanel LEFT JOIN tblCompleteTaxRequirements ON tblCoreSiteInformationPanel.[CENTER NBR] = tblCompleteTaxRequirements.[Site Number]) LEFT JOIN tblConstructionSchedule ON tblCoreSiteInformationPanel.[CENTER NBR] = tblConstructionSchedule.[Site Number]
WHERE (((tblConstructionSchedule.Open)>=Now() And (tblConstructionSchedule.Open)<=Now()+90) AND ((tblCompleteTaxRequirements.Complete)=False) AND ((tblCoreSiteInformationPanel.[Corporate Store or Franchise Store])="Corporate Store") AND ((tblConstructionSchedule.Category)="N"));

