SELECT qurReportingBase.[Project Number], qurReportingBase.DIVISION, qurReportingBase.[Corporate or Franchise], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.ST, qurReportingBase.Location, qurReportingBase.Category, qurReportingBase.[Project Manager], qurReportingBase.[Store Planner], qurReportingBase.Open, tblTakeoff.[Takeoff Item Number], tblTakeoff.[Item Number], tblTakeoff.[Item Account Number], tblTakeoff.[Item Description], tblTakeoff.[Item Cost], tblTakeoff.[Item Quantity], tblTakeoff.[Item Extended Cost]
FROM qurReportingBase LEFT JOIN tblTakeoff ON qurReportingBase.[Project Number] = tblTakeoff.[Project Number]
WHERE (((qurReportingBase.[Corporate or Franchise])="C") AND ((qurReportingBase.Open) Between Date() And Date()+21) AND ((tblTakeoff.[Item Quantity])>0) AND ((qurReportingBase.[Project Status])="A") AND ((qurReportingBase.[Plexi Ordered]) Is Null) AND ((tblTakeoff.[Vendor Name])="DC Plexiglass"));

