SELECT tblConstruction.Contractor, Count(qurCoreProjectInformationPanel.[Site Number]) AS [CountOfSite Number], Sum(tblContract.[Contract Amount]) AS [SumOfContract Amount], [Change Order One Amount]+[Change Order Two Amount]+[Change Order Three Amount]+[Change Order Four Amount]+[Change Order Five Amount]+[Change Order Six Amount]+[Change Order Seven Amount]+[Change Order Eight Amount]+[Change Order Nine Amount]+[Change Order Ten Amount]+[Change Order Eleven Amount]+[Change Order Twelve Amount]+[Change Order Thirteen Amount]+[Change Order Fourteen Amount]+[Change Order Fifteen Amount] AS [CO Sum]
FROM (qurCoreProjectInformationPanel INNER JOIN tblConstruction ON qurCoreProjectInformationPanel.[Project Number] = tblConstruction.[Project Number]) INNER JOIN tblContract ON qurCoreProjectInformationPanel.[Project Number] = tblContract.[Project Number]
WHERE (((Year([Open]))=Year(Date())) AND ((qurCoreProjectInformationPanel.[Project Status])="A") AND ((qurCoreProjectInformationPanel.Category)=[Enter Store Type (N, R, C, or P)]))
GROUP BY tblConstruction.Contractor, [Change Order One Amount]+[Change Order Two Amount]+[Change Order Three Amount]+[Change Order Four Amount]+[Change Order Five Amount]+[Change Order Six Amount]+[Change Order Seven Amount]+[Change Order Eight Amount]+[Change Order Nine Amount]+[Change Order Ten Amount]+[Change Order Eleven Amount]+[Change Order Twelve Amount]+[Change Order Thirteen Amount]+[Change Order Fourteen Amount]+[Change Order Fifteen Amount];

