SELECT qurReportingBase.[Project Number], qurReportingBase.DIVISION, qurReportingBase.[Corporate or Franchise], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.ST, qurReportingBase.Location, qurReportingBase.Category, qurReportingBase.[Project Manager], qurReportingBase.[Store Planner], qurReportingBase.[Fixture Relocation], qurReportingBase.[Store Format], qurReportingBase.[Fixtures Type to Use], qurReportingBase.[Construction Started], qurReportingBase.Open
FROM qurReportingBase
WHERE (((qurReportingBase.[Store Format])="A" Or (qurReportingBase.[Store Format])="B" Or (qurReportingBase.[Store Format])="C") AND ((qurReportingBase.Open) Between Date() And Date()+120)) OR (((qurReportingBase.Location)="M" Or (qurReportingBase.Location)="D") AND ((qurReportingBase.Category)<>"P") AND ((qurReportingBase.[Store Format])="D") AND ((qurReportingBase.Open) Between Date() And Date()+120) AND ((qurReportingBase.[Lifestyle Center])=False)) OR (((qurReportingBase.Location)="S") AND ((qurReportingBase.Category)<>"P") AND ((qurReportingBase.[Store Format])="D") AND ((qurReportingBase.Open) Between Date() And Date()+120) AND ((qurReportingBase.[Lifestyle Center])=True));

