SELECT tblCoreSiteInformationPanel.[CENTER NBR], tbl_Stores_Master.[OPEN-CLOSED], tblCoreSiteInformationPanel.DIVISION, tblCoreSiteInformationPanel.REGION, tblCoreSiteInformationPanel.[COMPANY NBR], tblCoreSiteInformationPanel.[CENTER NAME], tblCoreSiteInformationPanel.[ADDRESS 1], tblCoreSiteInformationPanel.[ADDRESS 2], tblCoreSiteInformationPanel.CITY, tblCoreSiteInformationPanel.State, tblCoreSiteInformationPanel.ZIP_CODE, tblCoreSiteInformationPanel.Telephone, tblCoreSiteInformationPanel.Location, tblCoreSiteInformationPanel.Format, tblCoreSiteInformationPanel.[SQUARE FOOTAGE], tblCoreSiteInformationPanel.[DATE OPENED], tbl_Stores_Master.[CONVERSION CENTER NUMBER] AS [Previous KK#], tbl_Stores_Master.LANDLORD AS Landlord, tbl_Stores_Master.[ANNUAL OCCUPANCY] AS [Annual Occupancy], tbl_Stores_Master.EXPIRATION AS [Lease Expiration], tbl_Stores_Master.[TRUE M TO M] AS [True M to M], tbl_Stores_Master.[ROLLING 12 SALES], tbl_Stores_Master.[R12 EBITDA], tbl_Stores_Master.[12mo BLY], tbl_Stores_Master.[3mo BLY], tbl_Stores_Master.[CY NBV TOTAL]
FROM tblCoreSiteInformationPanel LEFT JOIN tbl_Stores_Master ON tblCoreSiteInformationPanel.[CENTER NBR] = tbl_Stores_Master.[CENTER NBR]
WHERE (((tbl_Stores_Master.[OPEN-CLOSED])="o"));

