SELECT Sum(IIf([CountCorp] Is Null,0,[CountCorp])) AS CORP, Sum(IIf([CountOC] Is Null,0,[CountOC])) AS OC, Sum(IIf([CountFran] Is Null,0,[CountFran])) AS FRAN, Sum(IIf([CountTotal] Is Null,0,[CountTotal])) AS TOTAL
FROM ((qurRealEstateRepresentativeSummaryCurrentYearTotal LEFT JOIN qurRealEstateRepresentativeSummaryCurrentYearCorp ON qurRealEstateRepresentativeSummaryCurrentYearTotal.Representative = qurRealEstateRepresentativeSummaryCurrentYearCorp.Representative) LEFT JOIN qurRealEstateRepresentativeSummaryCurrentYearFran ON qurRealEstateRepresentativeSummaryCurrentYearTotal.Representative = qurRealEstateRepresentativeSummaryCurrentYearFran.Representative) LEFT JOIN qurRealEstateRepresentativeSummaryCurrentYearOC ON qurRealEstateRepresentativeSummaryCurrentYearFran.Representative = qurRealEstateRepresentativeSummaryCurrentYearOC.Representative;

