SELECT qurReportingBase.[Actual Open], qurReportingBase.Open, qurReportingBase.Turnover, qurReportingBase.[COMPANY NBR] AS [Co No], qurReportingBase.Inventory AS Inv, qurReportingBase.[Lease Status] AS Status, qurReportingBase.[SQUARE FOOTAGE] AS SqFt, qurReportingBase.DIVISION AS Div, qurReportingBase.[Site Number] AS KK, qurReportingBase.[CENTER NAME] AS [Center Name], qurReportingBase.CITY AS City, qurReportingBase.ST AS State, qurReportingBase.Location AS Type, qurReportingBase.Representative AS Rep, [New Store data input].REProj AS [First Year Field Sales Projection], [New Store data input].[Buxton Sales Forcast yr1] AS [Forum Analytics], tblFinancials.[Real Estate Estimate Total] AS [Const Cost], tblContract.[Cash Allowance Amount] AS [Cash Allowance], [New Store data input].IRR
FROM ((qurReportingBase LEFT JOIN tblFinancials ON qurReportingBase.[Project Number] = tblFinancials.[Project Number]) LEFT JOIN tblContract ON qurReportingBase.[Project Number] = tblContract.[Project Number]) LEFT JOIN [New Store data input] ON qurReportingBase.[Site Number] = [New Store data input].KK
WHERE (((qurReportingBase.[Project Status])=[Enter A, H, or D (Alive, Hold, Dead)]) AND ((qurReportingBase.Category)=[Enter N, C, or R (New, Remodel, Relocation)]) AND ((Year([Open]))=[Enter Year]))
ORDER BY qurReportingBase.[Actual Open], qurReportingBase.Open;

