SELECT qurReportingBase.[Project Number], qurReportingBase.DIVISION, qurReportingBase.[Corporate or Franchise], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.ST, qurReportingBase.Location, qurReportingBase.Category, qurReportingBase.[Project Manager], qurReportingBase.[Store Planner], qurReportingBase.[MP Completed], qurReportingBase.Open
FROM qurReportingBase
WHERE (((qurReportingBase.Category)="C" Or (qurReportingBase.Category)="N" Or (qurReportingBase.Category)="R") AND ((qurReportingBase.[MP Completed]) Is Not Null) AND ((qurReportingBase.Open)>=#1/1/2014#) AND ((qurReportingBase.[Project Status])="A") AND ((qurReportingBase.[Sign Drawings Sent]) Is Null)) OR (((qurReportingBase.Category)="C" Or (qurReportingBase.Category)="N" Or (qurReportingBase.Category)="R") AND ((qurReportingBase.[MP Completed]) Is Not Null) AND ((qurReportingBase.Open)>=#1/1/2014#) AND ((qurReportingBase.[Project Status])="A") AND ((qurReportingBase.[Sign Quote Sent]) Is Null));

