SELECT tblConstructionSchedule.[Project Manager], tblCoreSiteInformationPanel.[CENTER NBR], tblCoreSiteInformationPanel.DIVISION, tblCoreSiteInformationPanel.DISTRICT, tblCoreSiteInformationPanel.REGION, tblCoreSiteInformationPanel.[CENTER NAME], tblCoreSiteInformationPanel.[ADDRESS 1], tblCoreSiteInformationPanel.[ADDRESS 2], tblCoreSiteInformationPanel.CITY, tblCoreSiteInformationPanel.ST, tblCoreSiteInformationPanel.ZIP_CODE, tblCoreSiteInformationPanel.COUNTRY_NAME, tblCoreSiteInformationPanel.Telephone, [CMF INFO].nbr_pos_register, IIf([CMF INFO].[COMPANY NBR]="23" Or [CMF INFO].[COMPANY NBR]="70","F","C") AS [Corporate or Franchise], IIf([CMF INFO].[LOCATION TYPE]="S" And [CMF INFO].[COMPANY NBR]="65","MB",[CMF INFO].[LOCATION TYPE]) AS Location, IIf([PRIMARY_DC]="001","Leetsdale",IIf([PRIMARY_DC]="003","Anderson",IIf([PRIMARY_DC]="004","Phoenix",IIf([PRIMARY_DC]="006","Canada",[PRIMARY_DC])))) AS [Distribution Center], IIf([PLANOGRAM_CODE]="21","A",IIf([PLANOGRAM_CODE]="23","B",IIf([PLANOGRAM_CODE]="24","C",IIf([PLANOGRAM_CODE]="25","D",[PLANOGRAM_CODE])))) AS Segment
FROM ((tblConstructionSchedule LEFT JOIN tblCoreSiteInformationPanel ON tblConstructionSchedule.[Site Number] = tblCoreSiteInformationPanel.[CENTER NBR]) LEFT JOIN [CMF INFO] ON tblConstructionSchedule.[Site Number] = [CMF INFO].[CENTER NBR]) LEFT JOIN dbo_CENTER_ACCESS_VIEW ON tblConstructionSchedule.[Site Number] = dbo_CENTER_ACCESS_VIEW.CENTER_NBR
WHERE (((IIf([CMF INFO].[COMPANY NBR]="23" Or [CMF INFO].[COMPANY NBR]="70","F","C"))="C") AND ((tblConstructionSchedule.Category)="C" Or (tblConstructionSchedule.Category)="N" Or (tblConstructionSchedule.Category)="R") AND ((Month([Open]))=Month(Date())-1) AND ((Year([Open]))=Year(Date())));

