SELECT qurReportingBase.[Site Number], qurReportingBase.[Project Number], qurReportingBase.DIVISION, qurReportingBase.[Corporate or Franchise], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.COUNTRY_NAME, qurReportingBase.Location, [State Abbreviations].State, tblCategory.Category, tblProjectManager.[Project Manager], tblStorePlanner.[Store Planner], qurReportingBase.Appropriation, tblTakeoff.[Vendor Name], tblTakeoff.[Category Name], tblTakeoff.[Category Number], tblTakeoff.[Takeoff Item Number], tblTakeoff.[Item Number], tblTakeoff.[Item Account Number], tblTakeoff.[Item Cost], tblTakeoff.[Item Quantity], tblTakeoff.[Item Extended Cost], IIf([Vendor Name]="idX Los Angeles" And [Comments] Is Not Null,[Item Description] & " (" & [Comments] & ")",[Item Description]) AS Description
FROM ((((qurReportingBase LEFT JOIN tblTakeoff ON qurReportingBase.[Project Number] = tblTakeoff.[Project Number]) LEFT JOIN [State Abbreviations] ON qurReportingBase.ST = [State Abbreviations].[State Abbrev]) LEFT JOIN tblCategory ON qurReportingBase.Category = tblCategory.Abbreviation) LEFT JOIN tblProjectManager ON qurReportingBase.[Project Manager] = tblProjectManager.Initials) LEFT JOIN tblStorePlanner ON qurReportingBase.[Store Planner] = tblStorePlanner.Initials
WHERE (((qurReportingBase.[Project Number])=Forms!frmCoreSiteInformationPanel!fsubCoreProjectInformationPanel!txtProjectNumber) And ((tblTakeoff.[Item Quantity])>0));

