SELECT tblCoreSiteInformationPanel_1.[CENTER NBR], tblCoreSiteInformationPanel_1.[Corporate Store or Franchise Store], tblCoreSiteInformationPanel_1.DIVISION, tblCoreSiteInformationPanel_1.REGION, tblCoreSiteInformationPanel_1.[COMPANY NBR], tblCoreSiteInformationPanel_1.[CENTER NAME], tblCoreSiteInformationPanel_1.[ADDRESS 1], tblCoreSiteInformationPanel_1.[ADDRESS 2], tblCoreSiteInformationPanel_1.CITY, tblCoreSiteInformationPanel_1.State, tblCoreSiteInformationPanel_1.ZIP_CODE, tblCoreSiteInformationPanel_1.[County Name], tblCoreSiteInformationPanel_1.Telephone, tblCoreSiteInformationPanel_1.Status, tblCoreSiteInformationPanel_1.[DATE OPENED], dbo_CENTER_ACCESS_VIEW.PRIMARY_DC, tblCoreSiteInformationPanel_1.Location
FROM tblCoreSiteInformationPanel AS tblCoreSiteInformationPanel_1 INNER JOIN dbo_CENTER_ACCESS_VIEW ON tblCoreSiteInformationPanel_1.[CENTER NBR] = dbo_CENTER_ACCESS_VIEW.CENTER_NBR
