SELECT qurReportingBase.[Site Number], qurReportingBase.[Project Number], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.COUNTRY_NAME, qurReportingBase.[Construction Started], qurReportingBase.[Construction Completed], qurReportingBase.[Merchandising Started], qurReportingBase.Open, [State Abbreviations].State, tblProjectManager.[Project Manager], tblBids.[Bidder One], tblBids.[Bidder Two], tblBids.[Bidder Three], tblBids.[Bidder Four], tblBids.[Bidder Five], tblBids.[Bidder Six], tblBids.[Bidder Seven], tblBids.[Bidder Eight], tblBids.[Bidder Nine], tblBids.[Bidder Ten], tblBids.[Bids Comment]
FROM ((qurReportingBase LEFT JOIN [State Abbreviations] ON qurReportingBase.ST = [State Abbreviations].[State Abbrev]) LEFT JOIN tblProjectManager ON qurReportingBase.[Project Manager] = tblProjectManager.Initials) LEFT JOIN tblBids ON qurReportingBase.[Project Number] = tblBids.[Project Number]
WHERE (((qurReportingBase.[Project Number])=[Forms]![frmCoreSiteInformationPanel]![fsubCoreProjectInformationPanel]![txtProjectNumber]));

