SELECT [CMF INFO].[CENTER NBR], Format$([CMF INFO].[CENTER NBR],"0") AS [Site Number], [CMF INFO].[CENTER NAME], [CMF INFO].[ADDRESS 1], [CMF INFO].[ADDRESS 2], [CMF INFO].ST, [CMF INFO].[MANAGER NAME], [CMF INFO].[DATE OPENED], [CMF INFO].[DATE CLOSED], [CMF INFO].[LOCATION TYPE], [CMF INFO].DIVISION, [CMF INFO].REGION, [CMF INFO].[COMPANY NBR], [CMF INFO].conv_center, [CMF INFO].[SQUARE FOOTAGE], [OPEN SITES].CITY, [OPEN SITES].ZIP_CODE, [OPEN SITES].STORE_TYPE, [OPEN SITES].LAT, [OPEN SITES].LON, Zip_County_DMA_Define.[County Name], Zip_County_DMA_Define.[DMA Name], [State Abbreviations].State, dbo_COUNTRY_CODES.COUNTRY_NAME, tbl_Stores_Master.[OPTION TO RENEW], tbl_Stores_Master.[STORE CLOSING], tbl_Stores_Master.[DATE CLOSING], dbo_CENTER_ACCESS_VIEW.DISTRICT, tbl_fixture_type.Format, tblSalesAreaSquareFootage.[Sales Area Square Footage], tblPreviousRemodelingProject.[Previous Full Remodeling Project], tblPreviousRemodelingProject.[Previous Partial Remodeling Project], IIf([CMF INFO].[OPEN-CLOSED]="C","Closed",IIf([CMF INFO].[OPEN-CLOSED]="O","Open")) AS Status, [PHONE AREA CODE] & "." & [PHONE EXCHANGE] & "." & [PHONE NBR] AS Telephone, IIf([CMF INFO].[LOCATION TYPE]="D","Downtown",IIf([CMF INFO].[LOCATION TYPE]="M","Mall",IIf([CMF INFO].[LOCATION TYPE]="S" And [CMF INFO].[COMPANY_NBR]=65,"Military Base",IIf([CMF INFO].[LOCATION TYPE]="S","Strip",IIf([CMF INFO].[LOCATION TYPE]="O","Outlet"))))) AS Location, IIf([COMPANY NBR]="23" Or [COMPANY NBR]="70","Franchise Store","Corporate Store") AS [Corporate Store or Franchise Store]
FROM (((((((([CMF INFO] LEFT JOIN [OPEN SITES] ON [CMF INFO].[CENTER NBR] = [OPEN SITES].SITE_NUMBER) LEFT JOIN Zip_County_DMA_Define ON [CMF INFO].[ZIP CODE] = Zip_County_DMA_Define.Zip) LEFT JOIN [State Abbreviations] ON [CMF INFO].ST = [State Abbreviations].[State Abbrev]) LEFT JOIN tbl_fixture_type ON [CMF INFO].[CENTER NBR] = tbl_fixture_type.KK) LEFT JOIN tblSalesAreaSquareFootage ON [CMF INFO].[CENTER NBR] = tblSalesAreaSquareFootage.[Site Number]) LEFT JOIN tblPreviousRemodelingProject ON [CMF INFO].[CENTER NBR] = tblPreviousRemodelingProject.[Site Number]) LEFT JOIN dbo_COUNTRY_CODES ON [CMF INFO].Cnt_code = dbo_COUNTRY_CODES.COUNTRY_CODE) LEFT JOIN dbo_CENTER_ACCESS_VIEW ON [CMF INFO].[CENTER NBR] = dbo_CENTER_ACCESS_VIEW.CENTER_NBR) LEFT JOIN tbl_Stores_Master ON [CMF INFO].[CENTER NBR] = tbl_Stores_Master.[CENTER NBR]
ORDER BY [CMF INFO].[CENTER NBR];

