SELECT tblProjectManager.[Project Manager], Count(qurCoreProjectInformationPanel.[Site Number]) AS [CountOfSite Number], Sum(tblContract.[Contract Amount]) AS [SumOfContract Amount], [Change Order One Amount]+[Change Order Two Amount]+[Change Order Three Amount]+[Change Order Four Amount]+[Change Order Five Amount]+[Change Order Six Amount]+[Change Order Seven Amount]+[Change Order Eight Amount]+[Change Order Nine Amount]+[Change Order Ten Amount]+[Change Order Eleven Amount]+[Change Order Twelve Amount]+[Change Order Thirteen Amount]+[Change Order Fourteen Amount]+[Change Order Fifteen Amount] AS [CO Sum]
FROM (qurCoreProjectInformationPanel INNER JOIN tblContract ON qurCoreProjectInformationPanel.[Project Number] = tblContract.[Project Number]) INNER JOIN tblProjectManager ON qurCoreProjectInformationPanel.[Project Manager] = tblProjectManager.Initials
WHERE (((Year([Open]))=Year(Date())) AND ((qurCoreProjectInformationPanel.[Project Status])="A") AND ((qurCoreProjectInformationPanel.Category)=[Enter Store Type (N, R, C, or P)]))
GROUP BY tblProjectManager.[Project Manager], [Change Order One Amount]+[Change Order Two Amount]+[Change Order Three Amount]+[Change Order Four Amount]+[Change Order Five Amount]+[Change Order Six Amount]+[Change Order Seven Amount]+[Change Order Eight Amount]+[Change Order Nine Amount]+[Change Order Ten Amount]+[Change Order Eleven Amount]+[Change Order Twelve Amount]+[Change Order Thirteen Amount]+[Change Order Fourteen Amount]+[Change Order Fifteen Amount];

