SELECT qurRealEstateRepresentativeSummaryNextYearTotal.Representative, IIf([CountCorp] Is Null,0,[CountCorp]) AS CORP, IIf([CountOC] Is Null,0,[CountOC]) AS OC, IIf([CountFran] Is Null,0,[CountFran]) AS FRAN, IIf([CountTotal] Is Null,0,[CountTotal]) AS TOTAL
FROM ((qurRealEstateRepresentativeSummaryNextYearTotal LEFT JOIN qurRealEstateRepresentativeSummaryNextYearCorp ON qurRealEstateRepresentativeSummaryNextYearTotal.Representative = qurRealEstateRepresentativeSummaryNextYearCorp.Representative) LEFT JOIN qurRealEstateRepresentativeSummaryNextYearFran ON qurRealEstateRepresentativeSummaryNextYearTotal.Representative = qurRealEstateRepresentativeSummaryNextYearFran.Representative) LEFT JOIN qurRealEstateRepresentativeSummaryNextYearOC ON qurRealEstateRepresentativeSummaryNextYearFran.Representative = qurRealEstateRepresentativeSummaryNextYearOC.Representative;

