SELECT [tbl_RSD Information].[RSD Name], [tbl_RSD Information].Address, [tbl_RSD Information].City, [tbl_RSD Information].[Home Phone Number], [tbl_RSD Information].[Cell Phone Number], [tbl_RSD Information].[Office Phone Number], [tbl_RSD Information].[Fax Number], [tbl_RSD Information].[E-Mail Address], [State Abbreviations].State, IIf([Title]="RFC","Regional Franchise Coordinator",IIf([Title]="RSD","Regional Sales Director")) AS [Actual Title], Left([Zip Code],5) AS [Normalized ZIP Code]
FROM [tbl_RSD Information] LEFT JOIN [State Abbreviations] ON [tbl_RSD Information].State = [State Abbreviations].[State Abbrev]
WHERE ((([tbl_RSD Information].Title)="RFC" Or ([tbl_RSD Information].Title)="RSD") And (([tbl_RSD Information].Division)=Forms!frmCoreSiteInformationPanel!txtDivision) And (([tbl_RSD Information].Region)=Forms!frmCoreSiteInformationPanel!txtRegion)) Or ((([tbl_RSD Information].Title)="RFC" Or ([tbl_RSD Information].Title)="RSD") And (([tbl_RSD Information].Division)=Forms!frmCoreSiteInformationPanelLicense!txtDivision) And (([tbl_RSD Information].Region)=Forms!frmCoreSiteInformationPanelLicense!txtRegion));

