SELECT tblConstructionSchedule.[Project Number], tblConstructionSchedule.Category, tblConstructionSchedule.Drawer, tblCoreSiteInformationPanel.[CENTER NAME], [Open]-66-[Days]-36 AS [Projected Drawings Started], DateAdd("m",-4,Date()) AS [Current Date Minus Four], Month([Current Date Minus Four]) AS [Current Date Minus Four Month], Year([Current Date Minus Four]) AS [Current Date Minus Four Year], DateAdd("m",-3,Date()) AS [Current Date Minus Three], Month([Current Date Minus Three]) AS [Current Date Minus Three Month], Year([Current Date Minus Three]) AS [Current Date Minus Three Year], DateAdd("m",-2,Date()) AS [Current Date Minus Two], Month([Current Date Minus Two]) AS [Current Date Minus Two Month], Year([Current Date Minus Two]) AS [Current Date Minus Two Year], DateAdd("m",-1,Date()) AS [Current Date Minus One], Month([Current Date Minus One]) AS [Current Date Minus One Month], Year([Current Date Minus One]) AS [Current Date Minus One Year], Date() AS [Current Date], Month([Current Date]) AS [Current Date Month], Year([Current Date]) AS [Current Date Year], DateAdd("m",+1,Date()) AS [Current Date Plus One], Month([Current Date Plus One]) AS [Current Date Plus One Month], Year([Current Date Plus One]) AS [Current Date Plus One Year], DateAdd("m",+2,Date()) AS [Current Date Plus Two], Month([Current Date Plus Two]) AS [Current Date Plus Two Month], Year([Current Date Plus Two]) AS [Current Date Plus Two Year], Month([Projected Drawings Started]) AS [Projected Drawings Started Month], Year([Projected Drawings Started]) AS [Projected Drawings Started Year], IIf([LOCATION TYPE]="S" And [COMPANY NBR]="65","MB",[LOCATION TYPE]) AS Location
FROM tblConstructionSchedule LEFT JOIN tblCoreSiteInformationPanel ON tblConstructionSchedule.[Site Number] = tblCoreSiteInformationPanel.[CENTER NBR]
WHERE (((tblConstructionSchedule.Category)="C" Or (tblConstructionSchedule.Category)="N" Or (tblConstructionSchedule.Category)="R") AND ((tblConstructionSchedule.[Project Status])="A") AND ((tblConstructionSchedule.[Landlord Drawings Sent]) Is Null))
ORDER BY tblConstructionSchedule.[Site Number];

