SELECT qurReportingBase.[Site Number], qurReportingBase.[Project Number], qurReportingBase.DIVISION, qurReportingBase.[Corporate or Franchise], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.COUNTRY_NAME, qurReportingBase.Location, [State Abbreviations].State, tblCategory.Category, tblProjectManager.[Project Manager], tblStorePlanner.[Store Planner], qurReportingBase.Appropriation, qurReportingBase.Open, tblFinancials.[Final Cost Completed]
FROM ((((qurReportingBase LEFT JOIN [State Abbreviations] ON qurReportingBase.ST = [State Abbreviations].[State Abbrev]) LEFT JOIN tblCategory ON qurReportingBase.Category = tblCategory.Abbreviation) LEFT JOIN tblProjectManager ON qurReportingBase.[Project Manager] = tblProjectManager.Initials) LEFT JOIN tblStorePlanner ON qurReportingBase.[Store Planner] = tblStorePlanner.Initials) LEFT JOIN tblFinancials ON qurReportingBase.[Project Number] = tblFinancials.[Project Number]
WHERE (((qurReportingBase.[Project Number])=[Forms]![frmCoreSiteInformationPanel]![fsubCoreProjectInformationPanel]![txtProjectNumber]));

