SELECT qurReportingBase.[Site Number], qurReportingBase.[Project Number], qurReportingBase.[CENTER NAME], qurReportingBase.CITY, qurReportingBase.COUNTRY_NAME, [State Abbreviations].State, tblTakeoff.[Vendor Name], tblTakeoff.[Category Name], tblTakeoff.[Category Number], tblTakeoff.[Item Number], tblTakeoff.[Item Account Number], tblTakeoff.[Item Cost], tblTakeoff.[Item Quantity], tblTakeoff.[Item Extended Cost], IIf([Vendor Name]="idX Los Angeles" And [Comments] Is Not Null,[Item Description] & " (" & [Comments] & ")",[Item Description]) AS Description
FROM (qurReportingBase LEFT JOIN tblTakeoff ON qurReportingBase.[Project Number] = tblTakeoff.[Project Number]) LEFT JOIN [State Abbreviations] ON qurReportingBase.ST = [State Abbreviations].[State Abbrev]
WHERE (((qurReportingBase.[Project Number])=Forms!frmCoreSiteInformationPanel!fsubCoreProjectInformationPanel!txtProjectNumber) And ((tblTakeoff.[Vendor Name])=Forms!frmCoreSiteInformationPanel!fsubCoreProjectInformationPanel!cboExportVendorTakeoff) And ((tblTakeoff.[Item Quantity])>0));

